import React from 'react';
import avatar from '../images/profile-avatar.jpg';
import { api } from '../utils/Api';

function Main(props) {
    const [userName, setUserName] = React.useState()
    const [userDescription, setUserDescription] = React.useState()
    const [userAvatar, setUserAvatar] = React.useState()
    const [cards, setCards] = React.useState([])

    React.useEffect(() => {
        api.getUserInfo()
        .then(res => {
            setUserName(res.name)
            setUserDescription(res.about)
            setUserAvatar(res.avatar)
        })
        api.getInitialCards()
        .then(res => {
            setCards(res)
        })
    }, [])


    return (
        <main className="content">
            <section className="profile">
                <div className="profile__info">
                    <img className="profile__avatar" src={avatar} style={{ backgroundImage: `url(${userAvatar})` }} alt="Аватар профиля Жак-Ив Кусто" />
                    <div className="profile__button-background">
                        <button type="button" className="profile__edit-avatar" aria-label="Редактировать аватар" onClick={props.onEditAvatar}></button>
                    </div> 
                    <h1 className="profile__name">{userName}</h1>
                    <p className="profile__role">{userDescription}</p>
                    <button type="button" className="profile__edit-button" aria-label="Редактировать профиль" onClick={props.onEditProfile}></button>
                </div>
                <button type="button" className="profile__add-button" onClick={props.onAddPlace}></button>
            </section>

            <section className="elements">
                <ul className="elements__list">
                    {cards.map((item) => {
                        return (
                            <li key={item._id} className="element">
                                <button type="button" className="element__delete-button" aria-label="Удалить"></button>
                                <img className="element__image" src={item.link} alt="" />
                                <div className="element__info">
                                    <h2 className="element__title">{item.name}</h2>
                                    <button type="button" className="element__like-button" aria-label="Лайк">{item.likes.length}</button>
                                </div>
                            </li>
                        )
                    })}
                </ul>
            </section>
        </main>
    )
}

export default Main
