import React from 'react';
import failure from '../images/failure.svg';
import success from '../images/success.svg';

function InfoTooltip(props) {
    return (
        <section className={`popup ${props.isOpen ? 'popup_opened' : ''} popup_type_${props.name}`}>
            <div className="popup__container popup__container_type_reg-status">
                <button type="button" className="popup__close" aria-label="Закрыть попап" onClick={props.onClose}></button>
                <div className="popup__reg-status">
                    <img src={props.isSuccess ? success : failure} alt={props.isSuccess ? "Картинка с галочкой" : "Картинка с крестиком"} className="popup__reg-status-image" />
                    <p className="popup__reg-status-text">{props.isSuccess ? "Вы успешно зарегистрировались!" : "Что-то пошло не так! Попробуйте ещё раз."}</p>
                </div>
            </div>
        </section>
    )

}
